import random

def isPrime(num):
	if num < 2:
		return False
	for i in range (2, num):
		if (num % i == 0): 
			return False
	return True
	
def elementsDoubled(L):
	return list(map(lambda x: 2*x, L))

def oddElementsSquared(L):
	return list(map(lambda x: x**2, filter(lambda x: x & 1 == 1, L)))
	
def primeElements(L):
	return list(filter(isPrime, L))
	

	
L = [random.randint(1,100) for i in range (1000)]
print("Random List\n",L)
print("Elements Doubled\n",elementsDoubled(L))
print("Odd Elements Squared\n",oddElementsSquared(L))
print("Prime Elements\n",primeElements(L))


